@prefix cu: <https://growgraph.dev/current#> .

cu:Appeal1 a fca:Appeal ;
    fca:hasAppealId "Appeal12345" ;
    fca:challenges cu:Judgement1 ;
    fca:appealsTo cu:CourtAixEnProvence .

cu:Judgement1 a fca:Judgement ;
    fca:isAssociatedWith cu:Conviction1 ;
    fca:heardAt cu:CourtAixEnProvence ;
    fca:contains cu:Trial1 ;
    fca:hasSeverity fca:Felony ;
    fca:hasPunishment cu:Imprisonment1 .

cu:Conviction1 a fca:Conviction ;
    fca:hasPunishment cu:Imprisonment1 ;
    fca:hasOffenseType fca:ViolentOffense ;
    fca:hasFact cu:Fact1 .

cu:Imprisonment1 a fca:Imprisonment ;
    fca:hasDuration "P3Y"^^xsd:duration .

cu:CourtAixEnProvence a fca:Court ;
    rdfs:label "Court of Appeal of Aix-en-Provence" .

cu:Trial1 a fca:Trial ;
    fca:hasParticipant cu:Defendant1 ;
    fca:contains cu:Fact2 ;
    fca:takesPlaceOn "2023-01-25"^^xsd:date ;
    fca:takesPlaceAt cu:CourtAixEnProvence .

cu:Defendant1 a fca:Suspect ;
    rdfs:label "M. [U] [V]" ;
    fca:hasRole fca:Convict ;
    fca:hasMaritalStatus fca:Single ;
    fca:hasProfession fca:NoProfession ;
    fca:possessesResource fca:NoResource ;
    fca:hasConviction cu:Conviction1 .

cu:Fact1 a fca:Fact ;
    rdfs:label "Violence with premeditation" ;
    fca:hasFact cu:Fact2 .

cu:Fact2 a fca:Fact ;
    rdfs:label "Resulted in incapacity of more than eight days" .

cu:Fact3 a fca:Fact ;
    rdfs:label "Previous convictions for threats and drug offenses from 2017 to 2023" .

cu:Victim1 a fca:Victim ;
    rdfs:label "Victim of the assault" .

cu:Assault1 a fca:Assault ;
    fca:hasSeverity fca:Felony ;
    fca:hasParticipant cu:Defendant1 ;
    fca:hasParticipant cu:Victim1 ;
    fca:hasOffenseType fca:ViolentOffense .

cu:Ineligibility1 a fca:IneligibilityPunishment ;
    rdfs:label "Five years prohibition from carrying a weapon" ;
    fca:ineligibilityDuration "P5Y"^^xsd:duration .

cu:Ineligibility2 a fca:IneligibilityPunishment ;
    rdfs:label "Five years prohibition of residence" ;
    fca:ineligibilityDuration "P5Y"^^xsd:duration .

cu:Confiscation1 a fca:MonetaryPunishment ;
    rdfs:label "Confiscation ordered as part of the judgment" .
